import React, { useEffect, useState } from 'react';
import {api} from '../utils/api.js'

function Main(props) {
  const [userName, setUserName] = useState('Юлия');
  const [userDescription, setUserDescription] = useState('исследоователь');
  const [userAvatar, setUserAvatar] = useState('');
  const [cardsList, setCards] = useState([])

  useEffect(()=>{
    api.getProfileInfo()
      .then((profileData) => {
          setUserName(profileData.name);
          setUserDescription(profileData.about)
          setUserAvatar(profileData.avatar)
      })
      .catch( (error) => {
          console.log(error);
      });
  })

  useEffect(()=>{
    api.getInitialCards()
      .then((cardsData) => {
          setCards(cardsData);
      })
      .catch( (error) => {
          console.log(error);
      });
  })



  return (
    <main className="content">
        <section className="profile">
            <div className = "profile__photo-area">
            <img src={userAvatar} alt="Фото профиля" className="profile__photo" />
            <button type="button" aria-label="кнопка-добавить" 
                    className="profile__edtn-button profile__edtn-button_photo"
                    onClick={props.onEditAvatar}/>
            </div>

            <div className="profile__info">
            <h1 className="profile__name">{userName}</h1>
            <button type="button" aria-label="кнопка-редактировать" 
                    className="profile__edtn-button profile__edtn-button_info"
                    onClick={props.onEditProfile}/>
            <p className = "profile__bio">{userDescription}</p>
            </div>

            <button type="button" aria-label="кнопка-добавить" 
                    className="profile__add-button"
                    onClick={props.onAddPlace}/>
        </section>

        <section className="photo-grid">
            <ul className="cards">
                {cardsList.map((card) => (
                    <li className="cards__item">
                    <button type="button" aria-label="кнопка-удалить" className="cards__delete-btn"></button>
                    <img src={card['link']} alt={card.name} className="cards__image"/>
                    <div className="cards__elements">
                      <h2 className="cards__text">{card.name}</h2>
                      <div className="card__like-area">
                        <button type="button" aria-label="кнопка-лайк" className="cards__like"></button>
                        <p className="cards__like-number">{card.likes.length}</p>
                      </div>

                    </div>
                  </li>)
                )}
            </ul>
        </section>
    </main>
  );
}

export default Main;
