import React from 'react';
import * as auth from '../auth'

class Register extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        email: '',
        password: '',
        confirmPassword: '',
      }
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = (e) => {
      const {name, value} = e.target;

      this.setState({
        [name]: value
      });
    }

    handleSubmit = (e) => {
      e.preventDefault();
      let {password, email} = this.state;
      auth.register(password, email)
      .then((res) => {
        if (res){
          this.setState({
            message: 'все отлично'
          }, () => {
            this.props.history.push('/signin');
          })
        } else {
          this.setState({
            message: 'Что-то пошло не так!'
          })
        }
      })
    }

    render(){
      return (
        <div className="login-signup">
            <h3 className="login-signup__title">
                Регистрация
            </h3>
            <form className="login-signup__form" name='login-signup-form' onSubmit={this.handleSubmit}>
                <input type="email" placeholder="Email" id="email" name="email" 
                className="login-signup__form-input login-signup__form-input_field_email" 
                required minLength="2"/>
                <span className="login-signup__form-error email-error"></span>
                <input type="password" placeholder="Пароль" id="password" name="password" 
                className="login-signup__form-input login-signup__form-input_field_password" required/>
                <span className="login-signup__form-error password-error"></span>

                <button type="submit" aria-label="кнопка-Зарегистрироваться"
                  className="login-signup__submit-btn"
                  value='Зарегистрироваться'>Зарегистрироваться
                </button>
                <p className="login-signup__login-area">Уже зарегистрированы? 
                  <a className="login-signup__login-area login-signup__link"> Войти</a>
                </p>
            </form>

        </div>
    );
    }

  }

  export default Register;
