import Header from './Header'
import Main from './Main'
import Footer from './Footer'
import PopupWithForm from './PopupWithForm'
import EditProfilePopup from './EditProfilePopup'
import EditAvatarPopup from './EditAvatarPopup'
import AddPlacePopup from './AddPlacePopup'
import ImagePopup from './ImagePopup'
import React, { useEffect, useState } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import {api} from '../utils/api.js';

function App () {
  const [cardsList, setCards] = useState([]);
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [currentUser, setCurrentUser] = useState('');

  useEffect(()=>{
    api.getProfileInfo()
      .then((userData) => {
          setCurrentUser(userData);
      })
      .catch( (error) => {
          console.log(error);
      });
  }, [])

  useEffect(()=>{
    api.getInitialCards()
      .then((cardsData) => {
          setCards(cardsData);
       })
      .catch( (error) => {
          console.log(error);
      });
  }, [])


  function handleEditProfileClick () {
    setEditProfilePopupOpen(true)
  }

  function handleAddPlaceClick () {
    setAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick () {
    setEditAvatarPopupOpen(true);
  }

  function handleCardClick (card) {
    setSelectedCard(card);
  }

  function closeAllPopups() {
    setEditProfilePopupOpen(false)
    setAddPlacePopupOpen(false)
    setEditAvatarPopupOpen(false)
    setSelectedCard(null)
  }

  function handleUpdateUser (formData) {
    api.editProfileInfo(formData)
      .then((userData) => {
        setCurrentUser(userData);
        setEditProfilePopupOpen(false)
       })
      .catch( (error) => {
          console.log(error);
      });
  }

  function handleUpdateAvatar (formLink) {
    api.editProfilePhoto(formLink)
      .then((userData) => {
        setCurrentUser(userData);
        setEditAvatarPopupOpen(false)
       })
      .catch( (error) => {
          console.log(error);
      });
  }

  function handleAddPlaceSubmit (formLink) {
    api.addNewCard(newCard)
      .then((userData) => {
        setCards(userData);
        setAddPlacePopupOpen(false)
       })
      .catch( (error) => {
          console.log(error);
      });
  }


  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  } 

  function handleCardDelete(card) {
    api.deleteCard(card._id)
        .then(() => {
            setCards((cards) => cards.filter((c) => c._id !== card._id));
    });
  } 

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="App">
        <div className="page">

          <Header/>

          <Main 
              cards={cardsList}
              onEditAvatar={handleEditAvatarClick}
              onEditProfile={handleEditProfileClick}
              onAddPlace={handleAddPlaceClick}
              onCardClick={handleCardClick}
              onCardLike={handleCardLike}
              onCardDelete={handleCardDelete}
          />

          <Footer/>

          <EditProfilePopup isOpen={isEditProfilePopupOpen} 
                            onClose={closeAllPopups} 
                            onUpdateUser={handleUpdateUser}/>

          <EditAvatarPopup isOpen={isEditAvatarPopupOpen} 
                           onClose={closeAllPopups} 
                           onUpdateAvatar={handleUpdateAvatar}/> 

          <AddPlacePopup isOpen={isAddPlacePopupOpen}
                         onClose={closeAllPopups}
                         onAddPlace={handleAddPlaceSubmit}/>

          <PopupWithForm name='submit-delete' title='Вы уверены?' buttonText='Да'>
          </PopupWithForm>

          {selectedCard &&<ImagePopup card={selectedCard} onClose={() => {closeAllPopups()}}/>}

        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
