import React from 'react';

function ImagePopup (props) {
    return (
        <div className={`popup popup_type_image ${props.isOpen ?'popup_opened': ''}`}>
            <div className="popup__overlay"></div>
            <div className="popup__image-container">
                <button type="button" className='popup__close-btn popup__close-btn_area_image'
                aria-label="кнопка-закрыть" onClick={props.onClose}/>
                <figure className="popup__image">
                    <img src={props.card.link} alt={props.card.name} className="popup__image-content" />
                    <figcaption className="popup__image-caption">{props.card.name}</figcaption>
                </figure>
            </div>
        </div>
      );
  } 

export default ImagePopup
