import React from 'react';
import { Link } from 'react-router-dom';


function Header({ userEmail, isSuccess, loggedIn}) {
  return (
    <header className="header">
      <div className="header__logo" />

      {isSuccess ? (
          <div className="header__auth">
            <Link to="signin" className="header__link header__link_signup">Войти</Link>
          </div>
        ) 
        : (loggedIn) ? (
          <div className="header__auth">
            <p className="header__user-email">{userEmail} </p>
            <Link to="signout" className="header__link header__link_logout"> Выйти</Link>
          </div>
        ) :(
          <div className="header__auth">
            <Link to="signup" className="header__link header__link_login">Регистрация</Link>
          </div>
        )
      }     
    </header>
  );
}

export default Header;

<button type="submit" aria-label="кнопка-Зарегистрироваться"
            className="login-signup__submit-btn"
            value='Зарегистрироваться'
            onSubmit={handleSubmit}>Зарегистрироваться
          </button>
