import Header from './Header'
import Main from './Main'
import Footer from './Footer'
import EditProfilePopup from './EditProfilePopup'
import EditAvatarPopup from './EditAvatarPopup'
import AddPlacePopup from './AddPlacePopup'
import ImagePopup from './ImagePopup'
import ConfirmationPopup from './ConfirmationPopup'
import Register from './Register'
import Login from './Login'
import ProtectedRoute from './ProtectedRoute'
import InfoTooltip from './InfoTooltip'
import React, { useEffect, useState } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import {api} from '../utils/api.js';
import {escKey} from '../utils/constant.js';
import * as auth from '../auth'
import {Route, Switch,Redirect, useHistory } from 'react-router-dom';

function App () {
  const [cardsList, setCards] = useState([]);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isDeleteConfirmPopupOpen, setIsDeleteConfirmPopupOpen] = useState(false);
  const [isImagePopupOpen, setIsImagePopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [selectedCardDelete, setSelectedCardDelete] = useState(null);
  const [currentUser, setCurrentUser] = useState({});
  const [loggedIn, setLoggedIn] = useState(false);
  const [isInfotooltipOpen, setisInfotooltipOpen] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const history = useHistory();
  useEffect(()=>{
    api.getProfileInfo()
      .then((userData) => {
          setCurrentUser(userData);
      })
      .catch( (error) => {
          console.log(error);
      });

    api.getInitialCards()
    .then((cardsData) => {
        setCards(cardsData);
      })
    .catch( (error) => {
        console.log(error);
    });

    checkToken();

  }, [])

  useEffect(() => {
    const close = (e) => {
      if(e.keyCode === escKey){
        closeAllPopups()
      }
    }
    document.addEventListener('keydown', close)
    return () => document.removeEventListener('keydown', close)
  },[])

  function handleEditProfileClick () {
    setIsEditProfilePopupOpen(true)
  }

  function handleAddPlaceClick () {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick () {
    setIsEditAvatarPopupOpen(true);
  }

  function handleDeleteButtonClick (card) {
    setIsDeleteConfirmPopupOpen(true);
    setSelectedCardDelete(card);
  }

  function handleCardClick (card) {
    setSelectedCard(card);
    setIsImagePopupOpen(true);   
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setIsEditAvatarPopupOpen(false)
    setIsDeleteConfirmPopupOpen(false)
    setIsImagePopupOpen(false);
    setisInfotooltipOpen(false);
  }

  function handleUpdateUser (formData) {
    api.editProfileInfo(formData)
      .then((userData) => {
        setCurrentUser(userData);
        setIsEditProfilePopupOpen(false)
       })
      .catch( (error) => {
          console.log(error);
      });
  }

  function handleUpdateAvatar (formLink) {
    api.editProfilePhoto(formLink)
      .then((userData) => {
        setCurrentUser(userData);
        setIsEditAvatarPopupOpen(false)
       })
      .catch( (error) => {
          console.log(error);
      });
  }

  function handleAddPlaceSubmit (cardForm) {
    api.addNewCard(cardForm)
      .then((newCard) => {
        setCards([newCard, ...cardsList]);
        setIsAddPlacePopupOpen(false)
       })
      .catch( (error) => {
          console.log(error);
      });
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.changeLikeCardStatus(card._id, !isLiked)
       .then((newCard) => {
          setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        })
       .catch( (error) => {
          console.log(error);
       });
  } 

  function handleCardDelete(card) {
    api.deleteCard(card._id)
        .then(() => {
            setCards((cards) => cards.filter((c) => c._id !== card._id));
            setIsDeleteConfirmPopupOpen(false);
        })
        .catch( (error) => {
          console.log(error);
       });

  }

  function handleLogin ({email, password}){
    auth.authorize(email, password)
      .then(data => {
        const jwt  = data.token;
        localStorage.setItem('jwt', jwt)
        setLoggedIn(true)
        history.push('/main')
      })
      .catch(error => console.log(error));

  }

  function handleRegister({password, email}) {
    auth.register(password, email)
      .then(res => {
        if (res){
          setIsSuccess(true)
          setisInfotooltipOpen(true);
          history.push('/signin');
        }
      })
      .catch(error => {
        setIsSuccess(false)
        setisInfotooltipOpen(true);
        console.log(error);
      })
  }


  function checkToken () {
    const jwt = localStorage.getItem('jwt');
    if (jwt) {
      auth.getContent(jwt)
        .then(data => console.log(data))
        .catch(error => console.log(error));

    }
    // если у пользователя есть токен в localStorage, 
    // эта функция проверит, действующий он или нет
    if (localStorage.getItem('jwt')){
      const jwt = localStorage.getItem('jwt');
      // здесь будем проверять токен

    }
  } 

  return (
    <CurrentUserContext.Provider value={currentUser}>
        <Header/>

        <Switch>
          <Route exact path="/signup">
            <Register handleRegister={handleRegister}/>
          </Route>
          <Route exact path="/signin">
            <Login handleLogin={handleLogin}/>
          </Route>
          <Main exact path="/main"
            cards={cardsList}
            onEditAvatar={handleEditAvatarClick}
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onCardClick={handleCardClick}
            onCardLike={handleCardLike}
            onCardDelete={handleDeleteButtonClick}
        />
        </Switch>


        <Footer/>

        <EditProfilePopup isOpen={isEditProfilePopupOpen} 
                          onClose={closeAllPopups} 
                          onUpdateUser={handleUpdateUser}/>

        <EditAvatarPopup isOpen={isEditAvatarPopupOpen} 
                          onClose={closeAllPopups} 
                          onUpdateAvatar={handleUpdateAvatar}/> 

        <AddPlacePopup isOpen={isAddPlacePopupOpen}
                        onClose={closeAllPopups}
                        onAddPlace={handleAddPlaceSubmit}/>

        <ConfirmationPopup card = {selectedCardDelete}
                            isOpen={isDeleteConfirmPopupOpen}
                            onCardDelete={handleCardDelete}
                            onClose={closeAllPopups}/>

        <ImagePopup card={selectedCard} 
                    isOpen={isImagePopupOpen} 
                    onClose={closeAllPopups}/>


        <InfoTooltip isSuccess={isSuccess} 
                     isOpen={isInfotooltipOpen}
                     onClose={closeAllPopups}/>
    </CurrentUserContext.Provider>
  );
}

export default App;
