import React from 'react';

function ImagePopup ({card, isOpen, onClose}) {
    return (
        <div onClick={handleCloseOnOverlay} className={`popup popup_type_image ${isOpen ? 'popup_opened' : ''}`}>
            <div className="popup__image-wrapper">
                <img className="popup__image" src={card.link} alt={card.name} />
                <p className="popup__image-caption">{card.name}</p>
                <button className="popup__close-button" onClick={onClose} type="button" aria-label="Закрыть всплывающее окно редактирования профиля" />
            </div>
        </div>
      );
  } 

export default ImagePopup
