import React from 'react';


function Card (props) {
    function handleClick() {
        props.onCardClick(props.card);
    }  

    return (
        <li className="cards__item">
            <button type="button" aria-label="кнопка-удалить" className="cards__delete-btn"></button>
            <img src={props.card.link} alt={props.card.name} 
                    onClick={() => {handleClick()}}
                    className="cards__image"/>
            <div className="cards__elements">
                <h2 className="cards__text">{props.card.name}</h2>
                <div className="card__like-area">
                    <button type="button" aria-label="кнопка-лайк" className="cards__like"></button>
                    <p className="cards__like-number">{props.card.likes.length}</p>
                </div>
            </div>
        </li>
      );
  } 

export default Card
