import React from 'react';
import { Link, withRouter } from 'react-router-dom';

class Login extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      username: '',
      password: ''
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }
  handleChange(e) {
    const {name, value} = e.target;
    this.setState({
      [name]: value
    });
  }
  handleSubmit(e){
    e.preventDefault();
  }
  render(){
    return(
      <div className="register">
            <h3 className="register__title">
                Войти
            </h3>
            <form className="register__form" name='register-form'>
                <input type="email" placeholder="Email" id="email" name="email" 
                className="register__form-input register__form-input_field_email" 
                required minLength="2"/>
                <span className="register__form-error email-error"></span>
                <input type="password" placeholder="Пароль" id="password" name="password" 
                className="register__form-input register__form-input_field_password" required/>
                <span className="register__form-error password-error"></span>

                <button type="submit" aria-label="кнопка-Зарегистрироваться"
                  className="register__submit-btn"
                  value='Зарегистрироваться'>Войти
                </button>
            </form>

        </div>
    )
  }
}

export default withRouter(Login);
