import React, { useEffect, useState } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import {api} from '../utils/api.js'
import Card from './Card'

function Main({onEditAvatar, onEditProfile, onAddPlace, onCardClick}) {
  const [userName, setUserName] = useState('');
  const [userDescription, setUserDescription] = useState('');
  const [userAvatar, setUserAvatar] = useState('');
  const [cardsList, setCards] = useState([]);

  const userData = React.useContext(CurrentUserContext);

  useEffect(()=>{
    api.getProfileInfo()
      .then((profileData) => {
          setUserName(profileData.name);
          setUserDescription(profileData.about)
          setUserAvatar(profileData.avatar)
      })
      .catch( (error) => {
          console.log(error);
      });

    api.getInitialCards()
      .then((cardsData) => {
          setCards(cardsData);
       })
      .catch( (error) => {
          console.log(error);
      });
  }, [])

  return (
    <main className="content">
        <section className="profile">
            <div className = "profile__photo-area">
            <img src={userData.avatar} alt="Фото профиля" className="profile__photo" />
            <button type="button" aria-label="кнопка-добавить" 
                    className="profile__edtn-button profile__edtn-button_photo"
                    onClick={onEditAvatar}/>
            </div>

            <div className="profile__info">
            <h1 className="profile__name">{userData.name}</h1>
            <button type="button" aria-label="кнопка-редактировать" 
                    className="profile__edtn-button profile__edtn-button_info"
                    onClick={onEditProfile}/>
            <p className = "profile__bio">{userData.about}</p>
            </div>

            <button type="button" aria-label="кнопка-добавить" 
                    className="profile__add-button"
                    onClick={onAddPlace}/>
        </section>

        <section className="photo-grid">
            <ul className="cards">
                {cardsList.map((cardItem) => (
                    <Card key={cardItem._id} card={cardItem} onCardClick={onCardClick}/>)
                )}
            </ul>
        </section>
    </main>
  );
}

export default Main;
