import React from 'react';

class PopupWithForm extends React.Component {
    constructor(props) {
      super(props);
    }

    render() {
      return (
        <div className={`popup popup_type_${this.props.name}`}>
            <div className="popup__overlay"></div>
            <div className="popup__container">
                <button type="button" className={`popup__close-btn popup__close-btn_area_${this.props.name}`}
                aria-label="кнопка-закрыть"/>
                <h3 className="popup__name">{this.props.title}</h3>
                <form className="popup__form" name={`${this.props.name}-form`} novalidate>
                </form>
            </div>
        </div>
      );
    }
  } 

export default PopupWithForm
