//import logo from './logo.svg';
import '../index.css';
import Header from './Header'
import Main from './Main'
import Footer from './Footer'
import PopupWithForm from './PopupWithForm'
import ImagePopup from './ImagePopup'
import Card from './Card'
import React from 'react';

function App (props) {
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
  const [selectedCard, setImagePopupOpen] = React.useState();

  function handleEditProfileClick () {
    setEditProfilePopupOpen(true)
  }

  function handleAddPlaceClick () {
    setAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick () {
    setEditAvatarPopupOpen(true);
  }

  function handleCardClick () {
    setImagePopupOpen(true);
  }

  function closeAllPopups() {
    setEditProfilePopupOpen(false)
    setAddPlacePopupOpen(false)
    setEditAvatarPopupOpen(false)
    setImagePopupOpen(false)
  }

  return (
    <div className="App">
      <div className="page">

        <Header/>

        <Main 
            onEditAvatar={() => {handleEditAvatarClick()}}
            onEditProfile={() => {handleEditProfileClick()}}
            onAddPlace={() => {handleAddPlaceClick()}}
            onCardClick={(card) => {handleCardClick(card)}}
        />

        <Footer/>

        <PopupWithForm name='profile' title='Редактировать профиль' 
                      isOpen = {isEditProfilePopupOpen} 
                      onClose={() => {closeAllPopups()}}>
          <input type="text" placeholder="Имя" name="name" id="name" 
            className="popup__form-input popup__form-input_field_name" 
            required minLength="2" maxLength="40"/>
          <span className="popup__form-error name-error"></span>
          <input type="text" placeholder="О Себе" name="about" id="about" 
            className="popup__form-input popup__form-input_field_bio"
            required minLength="2" maxLength="200" />
          <span className="popup__form-error about-error"></span>
          <button type="submit" aria-label="кнопка-сохранить" 
            className="popup__submit-btn" value="Сохранить">Сохранить
          </button>
        </PopupWithForm>

        <PopupWithForm name='cards' title='Новое место' 
                      isOpen = {isAddPlacePopupOpen}
                      onClose={() => {closeAllPopups()}}>

          <input type="text" placeholder="Название" id="cardName" name="name" 
            className="popup__form-input popup__form-input_field_image-name" 
            required minLength="2" maxLength="30"/>
          <span className="popup__form-error cardName-error"></span>
          <input type="url" placeholder="Ссылка на картинку" id="cardLink" name="link" 
            className="popup__form-input popup__form-input_field_image-src" required/>
          <span className="popup__form-error cardLink-error"></span>
          <button type="submit" aria-label="кнопка-сохранить" 
            className="popup__submit-btn" value="Создать">Создать
          </button>

        </PopupWithForm>

        <PopupWithForm name='avatar' title='Обновить аватар' 
                      isOpen = {isEditAvatarPopupOpen}
                      onClose={() => {closeAllPopups()}}>

          <input type="url" placeholder="Ссылка на фотографию" id="link" name="link" 
              className="popup__form-input popup__form-input_field_avatar" required/>
          <span className="popup__form-error link-error"></span>
          <button type="submit" aria-label="кнопка-сохранить" 
              className="popup__submit-btn"
              value="Сохранить">Сохранить
          </button>
        </PopupWithForm>

        <PopupWithForm name='submit-delete' title='Вы уверены?'>
          <button type="submit" aria-label="кнопка-подтвердить" 
              className="popup__submit-btn">Да
          </button>
        </PopupWithForm>

        <ImagePopup card={selectedCard} onClose={() => {closeAllPopups()}}>

        </ImagePopup>
      </div>
    </div>
  );
}

export default App;
